   pub authority: Pubkey,
   pub custom_obj: CustomObject,
}

pub fn init(ctx: Context<Initialize>, custom_obj: CustomObj) -> ProgramResult{
  ctx,accounts,user_account.authority = ctx.accounts.user.key();
