E.g.
```.rs
#[test]
fn wtf() {
    #[derive(Debug)]
    struct Foo<T> {
        thing: T,
    }

    impl<T> std::ops::Deref for Foo<T> {
        type Target = T;

        fn deref(&self) -> &Self::Target {
            &self.thing
        }
    }
    let foo = Foo { thing: 123 };
    // This type-annotation triggers a deref()
    let x: &u32 = &foo;

    eprintln!("{:?}", x);
}
