// the function
fn create_pda(
  creator: &AccountInfo,
  pda_account: AccountInfo,
  sys_prog_account: AccountInfo,
  program_id: &Pubkey,
  fee: u64,
  id: u32,
  pda_bump: u8,
) {

  let pda_space = 41;
  let id_string = id.to_string();
  let id_seed = id_string.as_bytes();

  // instruction for create race pda account
  let create_account_instruction = &system_instruction::create_account(
      &creator.key, // payer
      &pda_account.key, // to
      fee, // lamports
      pda_space, // space in bytes
      &program_id);

  // we send the transaction and sign it
  invoke_signed(
      create_account_instruction,
      &[
          sys_prog_account,
          creator.clone(),
          pda_account,
      ],
      &[&[&id_seed, &[pda_bump]]]
  );
}

// how I call
create_pda(caller, pda_account, sys_prog_account, program_id, fee.into(), id, pda_bump);
