export async function updateDataInstruction(
    data: data,
    signerKey?: web3.PublicKey
):Promise<web3.TransactionInstruction>{
    const dataProgramId =  new web3.PublicKey(METAPLEX);

    const metadataBuffer = Buffer.from(serialize(DATA_SCHEMA, data));

    const keysSet = [
        {
          pubkey: signerKey,
          isSigner: false,
          isWritable: true,
        },
        {
          pubkey: data.mint,
          isSigner: false,
          isWritable: false,
        },
        {
          pubkey: metadata.updateAuthority,//mintAuthority
          isSigner: true,
          isWritable: false,
        },
        {
          pubkey: web3.SystemProgram.programId,
          isSigner: false,
          isWritable: false,
        },
        {
          pubkey: web3.SYSVAR_RENT_PUBKEY,
          isSigner: false,
          isWritable: false,
        }
      ];

    const instruction = new web3.TransactionInstruction({
        keys: keysSet,
        programId: dataProgramId,
        data: metadataBuffer,
    });

    return instruction;
}
